import{Timer}from "./timer.min.js";export class Countdown extends Timer{constructor(secsRemaining,doEachSec,doAtEnd){super(1000);this.secsRemaining=secsRemaining;super.start(()=>{this.secsRemaining-=1;if(doEachSec){doEachSec(this.secsRemaining)}if(this.secsRemaining==0){super.cancel();if(doAtEnd){doAtEnd()}}})}get seconds(){return this.secsRemaining}end(){super.cancel()}}