export class Modal{constructor(modal){this._modal=modal;this._els=this._modal.children;this._header=this._els[0];this._body=this._els[1];this._closeBtn=this._header.children[0]}get body(){return this._body}get closeBtn(){return this._closeBtn}get hdrText(){return this._header.children[1].textContent}set hdrText(text){this._header.children[1].textContent=text}isClosed(){return this._modal.classList.contains("hidden")}open(){this._modal.classList.remove("hidden")}doOnClose(cb){this._closeBtn.addEventListener("click",()=>{this._modal.classList.add("hidden");if(cb){cb()}})}get modal(){return this._modal}}